import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";

function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}

class Board extends React.Component {
  renderSquare(i) {
    return (
      <Square
        value={this.props.squares[i]}
        onClick={() => this.props.onClick(i)}
      />
    );
  }

  renderRow(n) {
    let row = [];
    for (let i = n; i < n + 5; i++) {
      row = row.concat(this.renderSquare(i));
      console.log(row);
    }
    return row;
  }

  renderRows() {
    let rows = [];
    for (let i = 0; i < 9; i += 3) {
      rows = rows.concat(<div className="board-row">{this.renderRow(i)}</div>);
      console.log(rows);
    }
    return rows;
  }

  render() {
    return <div>{this.renderRows()}</div>;
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      history: [
        {
          squares: Array(9).fill(null),
        },
      ],
      stepNumber: 0,
      xIsNext: true,
      isIncreasing: true,
    };
  }

  handleClick(i) {
    const locations = [
      [1, 1],
      [2, 1],
      [3, 1],
      [1, 2],
      [2, 2],
      [3, 2],
      [1, 3],
      [2, 3],
      [3, 3],
    ];

    const history = this.state.history.slice(0, this.state.stepNumber + 1);
    const current = history[history.length - 1];
    const squares = current.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? "X" : "O";
    this.setState({
      history: history.concat([
        {
          squares: squares,
          location: locations[i],
        },
      ]),
      stepNumber: history.length,
      xIsNext: !this.state.xIsNext,
    });
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: step % 2 === 0,
    });
  }

  sortHandler() {
    this.setState({
      isIncreasing: !this.state.isIncreasing,
    });
  }

  render() {
    const history = this.state.history;
    const current = history[this.state.stepNumber];
    const winner = calculateWinner(current.squares);

    let moves = history.map((step, move) => {
      const desc = move
        ? "Go to move #" + move + " - " + history[move].location
        : "Go to game start";
      return (
        <li key={move}>
          <button
            style={{ fontWeight: this.state.stepNumber === move ? "bold" : "" }}
            onClick={() => this.jumpTo(move)}
          >
            {desc}
          </button>
        </li>
      );
    });

    let status;
    if (winner) {
      status = "Winner: " + winner;
    } else {
      status = "Next player: " + (this.state.xIsNext ? "X" : "O");
    }

    return (
      <div className="game">
        <div className="game-board">
          <Board
            squares={current.squares}
            onClick={(i) => this.handleClick(i)}
          />
        </div>
        <div className="game-info">
          <div>{status}</div>
          <button onClick={() => this.sortHandler()}>Sort</button>
          <ol>{this.state.isIncreasing ? moves : moves.reverse()}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Game />);

function winHandler(squares, currentIndex, limit) {
  if (currentIndex === Math.sqrt(squares.length)) {
    return false;
  }

  if (limit === 0) {
    return true;
  }
  if (squares[currentIndex] === squares[currentIndex + 1]) {
    return winHandler(squares, currentIndex + 1, --limit);
  }

  return false;
}

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  //   for (let i = 0; i < lines.length; i++) {
  // const [a, b, c] = lines[i];
  // if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
  //   return squares[a];
  // }
  //   }
  for (let i = 0; i < squares.length; i++) {
    return winHandler(squares, i, 3);
  }

  return null;
}
